mapper.select:

select * 
from(
    select rownum rn, oid, member_mid, ostate, odate, imp_uid
    from orders
    where rownum <= #{pageNum} * #{amount} 


	and member_mid like '%'||#{keyword}||'%'



    order by odate desc
    )
where rn > (#{pageNum} -1) * #{amount}

mapper.update:
	UPDATE MEMBER 
       SET MPOINT = 
                   (
                    select mpoint from member where mid=#{mid})-(SELECT SUM(SAVEPOINT)
                      FROM ORDERLIST
                     WHERE OID = #{oid}
                     ) 
     WHERE MID = #{mid}
