mapper.select:

select * 
from(
    select rownum rn, mid, mname, mrole
    from member
    where rownum <= #{pageNum} * #{amount} 


	and mid like '%'||#{keyword}||'%'



    order by mjoindate desc
    )
where rn > (#{pageNum} -1) * #{amount}
