mapper.select:
select *
from product p
where p.pid=#{pid}

SELECT p.*
FROM
(select rownum rn, pid, pname, price, img1
from
(select *
from product
where lcategory = #{lcategory}

	and scategory =#{scategory}

)
) p

	WHERE p.rn > (#{param1.pageNum} -1) * #{param1.amount} and p.rn <= #{param1.pageNum} * #{param1.amount}

select count(*)
from product
where lcategory = #{lcategory}

	and scategory =#{scategory}
